import React, { Component } from 'react'
import { Tabs } from 'antd-mobile';
import {withRouter} from 'react-router-dom';
import './type.css'
import http from '../../utils/http'
class Type extends Component {
    state={
        tabs:[
                { title: '蛋糕', sub: '1' },
                { title: '冰淇淋', sub: '2' },
                { title: '咖啡下午茶', sub: '3' },
                { title: '面包', sub: '4' },
                { title: '常温蛋糕', sub: '5' },
                { title: '设计师礼品', sub: '6' },
            ],
        type:'cake'//默认是cake
    }
    componentWillMount(){
        const data=  http.get(`/good/type/`+this.state.type)
        data.then(res=>{
            // console.log(res.data);
            this.setState({typedata:res.data})
        })
    }
    add=(gid)=>{
        // console.log(this.state.datamsg[0]);
        //根据商品id先查询这个商品信息，然后加到购物车
          http.post('/shopcar/addgoods',{
              gid:this.state.datamsg[0].gid,
              img:this.state.datamsg[0].img,
              cname:this.state.datamsg[0].chtitle,
              ename:this.state.datamsg[0].egtitle,
              weight:this.state.weight,
              weight1:this.state.datamsg[0].weight1,
              weight2:this.state.datamsg[0].weight2,
              weight3:this.state.datamsg[0].weight3,
              weight4:this.state.datamsg[0].weight4,
              price:this.state.datamsg[0].price}).then(res=>{
            console.log(res);
          //成功后弹出提醒
          if(res.flag){
            successToast()
          }
          })
      }
    //不同类型商品跳转不同页面
    showdata=(type)=>{
        console.log(this.state.typedata);
        let typename='';
        let typrtitle='';
        switch(type){
            case 'cake':typename='蛋糕';typrtitle='新鲜乳脂奶油蛋糕';
                break;
            case 'icecream':typename='冰淇淋';typrtitle='10天生命的意式杰拉朵冰淇淋';
                break;
            case 'tea':typename='奶羹和雪酪';typrtitle='奶羹和雪酪';
                break;
            case 'bread':typename='面包';typrtitle='面包';
                break;
        }
        return (
            <div className='list-pro-box'>
                <div className='pro-list-title' id='list-title-1'>
                    <h2>
                        {typename}
                        <span>{typrtitle}</span>
                    </h2>
                </div>
                <div className='list-box'>
                    <ul>
                        {this.state.typedata.map(item=>(
                            <li id='list-goods-' onClick={this.gogoods.bind(null,item.gid)} key={item.gid}>
                                <div className='list-goods-pdleft'>
                                    <div className='list-pro-content'>
                                        <a title={item.chtitle}>
                                            <div className='list-pro-img'>
                                                <img src={item.img}></img>
                                            </div>
                                            <h3>
                                            {item.egtitle}
                                            <span>{item.chtitle}</span>
                                            </h3>
                                            <span className='price'>¥198.00/454g(1.0磅)</span>
                                            <div className='label'>
                                                <img src='https://m.21cake.com/upload/images/1e22720bd656f7bac246159010c7e1f4.png'></img>
                                            </div>
                                        </a>
                                        <a className='cart-add' name='cart-add_1318' data-id='1318' onClick={this.add(item.gid)}>
                                            <img src='https://static.21cake.com//themes/wap/img/list-cart.png'></img>
                                        </a>
                                    </div>
                                </div>
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
            )
    }
    //对应商品跳转到详情页面
    gogoods=(gid)=>{
        this.props.history.push(`/goods/${gid}`)
    }
    
    render() {
        const {typedata}=this.state;
        return (
            <div className="type">
                <Tabs tabs={this.state.tabs}
                initialPage={0}
                //高亮字体颜色
                tabBarActiveTextColor={'#442818'}
                onChange={(tab, index) => { console.log('onChange', index, tab); }}
                onTabClick={(tab, index) => { console.log('onTabClick', index, tab); }}
                >
                    {typedata ? this.showdata(this.state.type) : null}
                    
                </Tabs>
                <div className='lost-bottom'>
                    <div>没了</div>
                </div>
                <div className='footer'>
                    <ul>
                    <li id='appDownloadLink'>
                        <a href='https://m.21cake.com/appdown.html'>下载APP</a>
                    </li>
                    <li>
                        <a >联系我们</a>
                    </li>
                    <li>
                        <a >全站公告</a>
                    </li>
                    <li>
                        <a >生产经营资质</a>
                    </li>
                    </ul>
                    <p>
                    Copyright© 21Cake蛋糕官网商城 2007-2020, 
                    <br></br>        
                    版权所有 
                    <br></br>  
                    京ICP备14006254号-1
                    <br></br>  
                    网站注册公司名称: 北京廿一客食品有限公司
                    <br></br>  
                    网站运营：廿一客（上海）电子商务有限公司
                    <br></br>  
                    上海廿一客食品有限公司分公司
                    <br></br>  
                    杭州廿一客食品有限公司
                    <br></br>  
                    广州廿一客食品有限公司
                    <br></br>  
                    北京廿一客食品有限公司
                    <br></br>  
                    深圳廿一客贸易有限公司
                    <br></br>  
                    天津廿一客食品有限公司
                    <br></br>  
                    广州廿一客食品贸易有限公司
                    <br></br>  
                    举实（上海）食品有限公司
                    </p>
                </div>
          
            </div>
        )
    }
}

Type = withRouter(Type)
export default Type
