import React , { Component } from 'react';
import { NavBar,Icon,Menu, ActivityIndicator,Drawer, List,Accordion,} from 'antd-mobile';
import 'antd-mobile/dist/antd-mobile.css';
// import './icon/iconfont/iconfont'
import './App.css'
import Home from './pages/Home/index';
import Type from './pages/Type/index';
import Person from './pages/Person/index'


export default class App extends Component {
  state={
    current:'/home',
    menu:[{
      title:'首页',
      path:'/home',
      Component:Home
    },{
      title:'分类',
      path:'/type',
      Component:Type
    },{
      title:'个人',
      path:'/person',
      Component:Person
    }],
    open: false,//抽屉
  }
  onChange = (key) => {
    console.log(key);
  }
  render() {
    console.log(this.state.menu);
    //抽屉
    const sidebar = (<List>
      {this.state.menu.map((i, index) => {
        if (index === 0) {
          return (<List.Item key={index}
            // thumb=""
            // multipleLine
          >{i.title}</List.Item>);
        }
        return (<List.Item key={index}
          // thumb=""
        >{i.title}</List.Item>);
      })}
    </List>);
    return (
      <div>
        <div className="title-box" >
          <NavBar
            mode="light"
            // icon={<i className="iconfont icon-title" ></i>}
            leftContent={[
              <i key="0" className="iconfont icon-title" ></i>,
              <i key="1" className="city"></i>,
              <span key="2" style={{ fontSize:'12px',color:'#442818' }}>广州</span>
            ]}
            onLeftClick={this.onOpenChange}
            rightContent={[
              <i key="0" className="top-message" ></i>,
              <i key="1" className="top-cart"></i>
            ]}
          >
          <i className="iconfont icon-header-center" type="left" style={{ width:"30px" }}></i>
        </NavBar>
        </div>
        <div className="container" >
        </div>
      </div>
      
    );
  }
}


class AccordionExmple extends React.Component {
  onChange = (key) => {
    console.log(key);
  }
  render() {
    return (
      <div style={{ marginTop: 10, marginBottom: 10 }}>
        <Accordion defaultActiveKey="0" className="my-accordion" onChange={this.onChange}>
          <Accordion.Panel header="Title 1">
            <List className="my-list">
              <List.Item>content 1</List.Item>
              <List.Item>content 2</List.Item>
              <List.Item>content 3</List.Item>
            </List>
          </Accordion.Panel>
          <Accordion.Panel header="Title 2" className="pad">this is panel content2 or other</Accordion.Panel>
          <Accordion.Panel header="Title 3" className="pad">
            text text text text text text text text text text text text text text text
          </Accordion.Panel>
        </Accordion>
      </div>
    );
  }
}

ReactDOM.render(<AccordionExmple />, mountNode);
